<node TEXT="28 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_443064877" CREATED="1436306217342" MODIFIED="1436306718432" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111128.htm">
<edge STYLE="bezier" COLOR="#00ffff" WIDTH="thin"/>
