<node TEXT="God has no spirit body, we have spirit body" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_739390854" CREATED="1443218162511" MODIFIED="1443218186845">
<edge STYLE="bezier" COLOR="#ff0000" WIDTH="thin"/>
