<node TEXT="Internal and External Gods" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_623598484" CREATED="1442654489257" MODIFIED="1444947770176">
