<node TEXT="True Father tried to describe Him" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_871420303" CREATED="1429691435968" MODIFIED="1433383862267">
