<node TEXT="Following true Father must have diplomatic power" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1037808177" CREATED="1433314063835" MODIFIED="1433314086947">
