<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="24 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_210680776" CREATED="1435961250843" MODIFIED="1436965148482" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111124.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
