<node TEXT="04 Sep 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_869692101" CREATED="1433556569551" MODIFIED="1433556587331" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-110904b.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
