<node TEXT="True Mother has to indemnify disciples rejection of Mary Magdalene" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1093250423" CREATED="1433556660621" MODIFIED="1433556669660">
