<node TEXT="http://www.tongil.org/ucbooks/smmspeaks/2012/20120928-Wife_of_Jesus.html" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_755134497" CREATED="1433556742295" MODIFIED="1433556912909" LINK="http://www.tongil.org/ucbooks/smmspeaks/2012/20120928-Wife_of_Jesus.html">
