<node TEXT="Relatives in the Moon clan" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_700675934" CREATED="1434922231864" MODIFIED="1434922544233">
