<node TEXT="Great granduncle Yun Guk Moon" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1225206944" CREATED="1433329235725" MODIFIED="1443995620539">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
