<node TEXT="Elder sister waited for TF and gave him food" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1640737715" CREATED="1438691876869" MODIFIED="1438691932170">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
