<node TEXT="Danbery prison" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_189991295" CREATED="1433315003484" MODIFIED="1433315010531">
