<node TEXT="21 Apr 2012&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1905877967" CREATED="1440798489696" MODIFIED="1440798521173" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon12/SunMyungMoon-120421.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
