<node TEXT="Start small and give increasingly to end up great" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1136170845" CREATED="1437051485504" MODIFIED="1437051491785">
