<node TEXT="09 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1448175146" CREATED="1435355445815" MODIFIED="1445378058020" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111109.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
