<node TEXT="Sister Kathy Rigney" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_408518946" CREATED="1433316423598" MODIFIED="1433316452269">
