<node TEXT="Hwang Sun-jo - President of UPF Korea" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1213674283" CREATED="1433316595938" MODIFIED="1433316607627">
