<node TEXT="Family members are aunts" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1634281448" CREATED="1441492490320" MODIFIED="1441492528564">
