<node TEXT="About helicopter accident" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1144652711" CREATED="1433317472742" MODIFIED="1433317484448">
