<node TEXT="First and last assembly in human history" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1442457332" CREATED="1433457189987" MODIFIED="1435008428042">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="05 Oct 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1870699638" CREATED="1433809574480" MODIFIED="1435008428041" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111005b.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
