<node TEXT="Blessed husbands and wives consecrate their relationship to God" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1281786512" CREATED="1443516876625" MODIFIED="1443561813107">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
