<node TEXT="02 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_715277067" CREATED="1435096679374" MODIFIED="1445549798622" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111102.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
