<node TEXT="Atop of that great mountain of hope in the distance awaits me." COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1002055780" CREATED="1435235951230" MODIFIED="1435236122836">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="05 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_995842300" CREATED="1435235969913" MODIFIED="1435236039671" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111105.htm">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<richcontent TYPE="DETAILS">

<html>
