<node TEXT="Three stages of registration" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_260133490" CREATED="1442527634059" MODIFIED="1442527645422">
