<node TEXT="if you make the worst place becomes the best place" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1288666652" CREATED="1438638433227" MODIFIED="1438638485547">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
