<node TEXT="Interpreting its name" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_417160933" CREATED="1439211068552" MODIFIED="1439211086218">
<edge STYLE="bezier" COLOR="#0000ff" WIDTH="thin"/>
