<node TEXT="Receive God&apos;s love to love your enemy" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1479244202" CREATED="1433318053572" MODIFIED="1433318066287">
<edge STYLE="bezier" COLOR="#ff00ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="18 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1431774781" CREATED="1434588812945" MODIFIED="1434588825661" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111118.htm">
<edge STYLE="bezier" COLOR="#ff00ff" WIDTH="thin"/>
