<node TEXT="Principle of Cain and Abel" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1220853465" CREATED="1433317690599" MODIFIED="1433317700864">
