<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="24 Nov 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_230291532" CREATED="1434433865863" MODIFIED="1434433882275" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-111124.htm">
