<node TEXT="Subject and Object" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_196799331" CREATED="1433885464538" MODIFIED="1433885554926">
