<node TEXT="Our current spirit self cannot respond to God" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1246040181" CREATED="1433318133345" MODIFIED="1433318144791">
