<node TEXT="Spiritual light colour" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1675887329" CREATED="1433318145056" MODIFIED="1433318155141">
