<node TEXT="Not necessary to experience receiving revelations" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1191410971" CREATED="1442789424825" MODIFIED="1442789469683">
<edge STYLE="bezier" COLOR="#ff00ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="10 Jul 2012&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1281867752" CREATED="1442789479015" MODIFIED="1444556030833" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon12/SunMyungMoon-120710.htm">
<edge STYLE="bezier" COLOR="#ff00ff" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<richcontent TYPE="DETAILS" HIDDEN="true">

<html>
