<node TEXT="Teaching Divine Principle - practise it" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1092584931" CREATED="1443000505641" MODIFIED="1443259595229">
