<node TEXT="03 Mar 2012&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_175902356" CREATED="1435474808004" MODIFIED="1444685346029" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon12/SunMyungMoon-120303.htm">
<edge STYLE="bezier" COLOR="#00ff00" WIDTH="thin"/>
