<node TEXT="How Universe maintains its equilibrium" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1265038116" CREATED="1433325642227" MODIFIED="1433325651963">
<edge STYLE="bezier" COLOR="#00ff00" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="10 Mar 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1815600542" CREATED="1435474898646" MODIFIED="1435474915674" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-110310a.htm">
