<node TEXT="Sickness, healing via praying" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1680538827" CREATED="1433326072417" MODIFIED="1433326076066">
