<node TEXT="Ocean Industries" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_458890788" CREATED="1433326517889" MODIFIED="1433326529025">
