<node TEXT="Inventions" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1719180418" CREATED="1433326531075" MODIFIED="1433326541721">
<edge STYLE="bezier" COLOR="#00ff00" WIDTH="thin"/>
