<node TEXT="King Garden and the Queen Garden (in Hawaii) are the Garden of Eden without a shadow of the fall" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_768445171" CREATED="1433327160963" MODIFIED="1433327164903">
<edge STYLE="bezier" COLOR="#00ff00" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<node TEXT="10 Jun 2011&#xa;&#xa;" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" ID="ID_1514299506" CREATED="1435727709023" MODIFIED="1444771872149" LINK="http://www.tparents.org/Moon-Talks/SunMyungMoon11/SunMyungMoon-110610.htm">
<edge STYLE="bezier" COLOR="#00ff00" WIDTH="thin"/>
<font NAME="SansSerif" SIZE="10" BOLD="false" ITALIC="false"/>
<richcontent TYPE="DETAILS">

<html>
