<node TEXT="Apostle Paul spoke about third heaven" COLOR="#000000" STYLE="as_parent" MAX_WIDTH="600" MIN_WIDTH="1" FOLDED="true" ID="ID_1190393242" CREATED="1438984609559" MODIFIED="1438984635931">
